mapper.insert:

update  memo_master set 


 mm_user_name = #{wname}
 ,mm_memo = #{wmemo}
 ,mm_wdate = Now()

where 1=1

and  mm_idx=${idx}


and  mm_manage_no=#{manage_no}

delete from  memo_master 
