and  mm_idx=${idx}

mapper.select:
		select *,(row_number() over()) as NO
  	from jijuk_2024
  	WHERE 1=1

	and sido_nm=#{sido_nm}


	and sgg_nm=#{sgg_nm}


	and emd_nm=#{emd_nm}


	and ri_nm=#{ri_nm}


	and jibun=#{jibun}


	AND regexp_replace(juso,'\s+',' ') LIKE '%${address}%' 
	limit 100

select *,(row_number() over()) as NO from jijuk_2024 WHERE 1=1
and sido_nm=#{sido_nm}
and sgg_nm=#{sgg_nm}
and (emd_nm=#{emd_nm} or ri_nm=#{emd_nm})
and jibun LIKE '%${jibun}%'
AND regexp_replace(juso,'\s+',' ') LIKE '%${address}%' limit 100

select * ,(row_number() over()) as NO ,j2.PNU, j2.JIBUN, j2.SIDO_NM, j2.SGG_NM, j2.EMD_NM, j2.RI_NM, j2.GOVER_OWN_YN, j2.JISANG_STATUS, j2.JIJUK_AREA, j2.JIBUN_FULL, j2.JIMOK_TEXT, j2.DOSIPLAN, j2.PIPE_OVERLAP_YN, jm.jm_permitted_yn as PERMITTED_YN, jm.jm_comple_yn as COMPLE_YN
from jijuk_2024 j2 left join jisang_master jm on jm.jm_pnu =j2.pnu WHERE 1=1
and j2.sido_nm=#{sido_nm}
and j2.sgg_nm=#{sgg_nm}
and j2.emd_nm=#{emd_nm}
and j2.ri_nm=#{ri_nm}
and j2.jibun=#{jibun}
AND regexp_replace(j2.juso,'\s+',' ') LIKE '%${address}%'

SELECT *
FROM jijuk_2024
