mapper.select:
select count(*) from ( SELECT (row_number() OVER()) AS RN, (row_number() OVER()) AS no, TRIM(CONCAT(dom_sido_nm, ' ', dom_sgg_nm, ' ',dom_emd_nm,' ',dom_ri_nm, ' ', dom_jibun)) AS address, dom_dopco_no as DOPCO_NO, dom_sido_nm as SIDO_NM, dom_sgg_nm as SGG_NM, dom_emd_nm as EMD_NM, dom_ri_nm as RI_NM, dom_jibun as JIBUN, dom_addrcode as ADDRCODE, dom_jisa as JISA, dom_dopco_status as DOPCO_STATUS, TO_CHAR(dom_DEUNGGI_DATE, 'YYYY-MM-DD') AS DEUNGGI_DATE, dom_pnu as PNU, dom_yongdo as YONGDO, dom_pipe_name as PIPE_NAME, dom_sun_gubun as SUN_GUBUN, dom_JIJUK_AREA AS JIJUK_AREA, dom_gover_own_yn as GOVER_OWN_YN, dom_dosiplan as DOSIPLAN, dom_jasan_no as JASAN_NO, dom_comple_yn as COMPLE_YN, dom_deunggi_no as DEUNGGI_NO, dom_deunggiso as DEUNGGISO, dom_jimok_text as JIMOK_TEXT, TO_CHAR(dom_CHUIDEUK_DATE, 'YYYY-MM-DD') AS CHUIDEUK_DATE FROM DOPCO_MASTER WHERE 1 = 1 AND dom_CANCEL_YN !='Y'
and dom.dom_jisa=#{jisa}
and dom.dom_jasan_no like '%${jasan_no}%'
and dom.dom_chuideuk_date::date between #{start_date}::date and #{end_date}::date
) r where 1=1
and r.address like '%${address}%'

select * from ( SELECT (row_number() OVER()) AS RN, (row_number() OVER()) AS no, dom_idx as idx, TRIM(CONCAT(dom_sido_nm, ' ', dom_sgg_nm, ' ',dom_emd_nm,' ',dom_ri_nm, ' ', dom_jibun)) AS address, dom_dopco_no as DOPCO_NO, dom_sido_nm as SIDO_NM, dom_sgg_nm as SGG_NM, dom_emd_nm as EMD_NM, dom_ri_nm as RI_NM, dom_jibun as JIBUN, dom_addrcode as ADDRCODE, dom_jisa as JISA, dom_dopco_status as DOPCO_STATUS, TO_CHAR(dom_DEUNGGI_DATE, 'YYYY-MM-DD') AS DEUNGGI_DATE, dom_pnu as PNU, dom_yongdo as YONGDO, dom_pipe_name as PIPE_NAME, dom_sun_gubun as SUN_GUBUN, dom_JIJUK_AREA AS JIJUK_AREA, dom_gover_own_yn as GOVER_OWN_YN, dom_dosiplan as DOSIPLAN, dom_jasan_no as JASAN_NO, dom_comple_yn as COMPLE_YN, dom_deunggi_no as DEUNGGI_NO, dom_deunggiso as DEUNGGISO, dom_jimok_text as JIMOK_TEXT, TO_CHAR(dom_CHUIDEUK_DATE, 'YYYY-MM-DD') AS CHUIDEUK_DATE FROM DOPCO_MASTER WHERE 1 = 1 and (dom_CANCEL_YN !='Y' or dom_cancel_yn IS NULL)
and dom.dom_jisa=#{jisa}
and dom.dom_jasan_no like '%${jasan_no}%'
and dom.dom_chuideuk_date::date between #{start_date}::date and #{end_date}::date
ORDER BY ${orderCol} ${desc}
) r where 1=1
and r.address like '%${address}%'
limit #{length} offset #{start}

select * from ( SELECT (row_number() OVER()) AS RN, (row_number() OVER()) AS no, TRIM(CONCAT(dom_sido_nm, ' ', dom_sgg_nm, ' ',dom_emd_nm,' ',dom_ri_nm, ' ', dom_jibun)) AS address, dom_dopco_no as DOPCO_NO, dom_sido_nm as SIDO_NM, dom_sgg_nm as SGG_NM, dom_emd_nm as EMD_NM, dom_ri_nm as RI_NM, dom_jibun as JIBUN, dom_addrcode as ADDRCODE, dom_jisa as JISA, dom_dopco_status as DOPCO_STATUS, TO_CHAR(dom_DEUNGGI_DATE, 'YYYY-MM-DD') AS DEUNGGI_DATE, dom_pnu as PNU, dom_yongdo as YONGDO, dom_pipe_name as PIPE_NAME, dom_sun_gubun as SUN_GUBUN, dom_JIJUK_AREA AS JIJUK_AREA, dom_gover_own_yn as GOVER_OWN_YN, dom_dosiplan as DOSIPLAN, dom_jasan_no as JASAN_NO, dom_comple_yn as COMPLE_YN, dom_deunggi_no as DEUNGGI_NO, dom_deunggiso as DEUNGGISO, dom_jimok_text as JIMOK_TEXT, TO_CHAR(dom_CHUIDEUK_DATE, 'YYYY-MM-DD') AS CHUIDEUK_DATE FROM DOPCO_MASTER WHERE 1 = 1 and (dom_CANCEL_YN !='Y' or dom_cancel_yn IS NULL)
and dom.dom_jisa=#{jisa}
and dom.dom_jasan_no like '%${jasan_no}%'
and dom.dom_chuideuk_date::date between #{start_date}::date and #{end_date}::date
AND dom_DOPCO_NO = #{dopco_no}
) r where 1=1
and r.address like '%${address}%'

select count(*) from ( SELECT *, TRIM(CONCAT(dom.dom_sido_nm, ' ', dom.dom_sgg_nm, ' ',dom.dom_emd_nm,' ',dom.dom_ri_nm, ' ', dom.dom_jibun)) AS address FROM dopco_master dom
WHERE 1 = 1
and dom.dom_jisa=#{jisa}
and dom.dom_jasan_no like '%${jasan_no}%'
and dom.dom_cancel_yn =#{cancel_yn}
and dom.dom_chuideuk_date::date between #{start_date}::date and #{end_date}::date
) r where 1=1
and r.address like '%${address}%'

select * from ( SELECT *, (row_number() OVER()) AS no, TRIM(CONCAT(dom.dom_sido_nm, ' ', dom.dom_sgg_nm, ' ',dom.dom_emd_nm,' ',dom.dom_ri_nm, ' ', dom.dom_jibun)) AS address FROM dopco_master dom
WHERE 1=1
and dom.dom_jisa=#{jisa}
and dom.dom_jasan_no like '%${jasan_no}%'
and dom.dom_chuideuk_date::date between #{start_date}::date and #{end_date}::date
ORDER BY ${orderCol} ${desc}
) r where 1=1
and r.address like '%${address}%'
limit #{length} offset #{start}

mapper.insert:
Insert into DOPCO_MASTER
( dom_DOPCO_NO, dom_SIDO_NM, dom_SGG_NM, dom_EMD_NM, dom_RI_NM, dom_JIBUN, dom_ADDRCODE
, dom_JISA, dom_DOPCO_STATUS, dom_YONGDO, dom_PIPE_NAME, dom_SUN_GUBUN, dom_JIJUK_AREA
, dom_GOVER_OWN_YN, dom_DOSIPLAN, dom_JASAN_NO, dom_COMPLE_YN, dom_DEUNGGI_DATE, dom_DEUNGGI_NO
, dom_DEUNGGISO, dom_PNU, dom_JIMOK_TEXT, dom_CHUIDEUK_DATE )
Values
( #{DOPCO_NO}, #{SIDO_NM},#{SGG_NM},#{EMD_NM},#{RI_NM},#{JIBUN#},#{ADDRCODE}
,#{JISA},#{DOPCO_STATUS},#{YONGDO},#{PIPE_NAME},#{SUN_GUBUN},#{JIJUK_AREA}
,#{GOVEROWNYN},#{DOSIPLAN},#{JASANNO},#{COMPLE_YN},to_date(#{DEUNGGI_DATE},'yyyy-mm-dd'),#{DEUNGGI_NO}
,#{DEUNGGISO},#{PNU},#{JIMOK_TEXT}, TO_DATE(#{CHUIDEUK_DATE}, 'yyyy-mm-dd') )

mapper.update:
UPDATE DOPCO_MASTER
SET

dom_SIDO_NM = #{SIDO_NM}
, dom_SGG_NM = #{SGG_NM}
, dom_EMD_NM = #{EMD_NM}
, dom_RI_NM = #{RI_NM}
, dom_JIBUN = #{JIBUN}
, dom_ADDRCODE = #{ADDRCODE}
, dom_JISA = #{JISA}
, dom_DOPCO_STATUS = #{DOPCO_STATUS}
, dom_YONGDO = #{YONGDO}
, dom_PIPE_NAME = #{PIPE_NAME}
, dom_SUN_GUBUN = #{SUN_GUBUN}
, dom_JIJUK_AREA = #{JIJUKAREA}
, dom_GOVER_OWN_YN = #{GOVEROWNYN}
, dom_DOSIPLAN = #{DOSIPLAN}
, dom_JASAN_NO = #{JASANNO}
, dom_COMPLE_YN =#{COMPLE_YN}
, dom_DEUNGGI_DATE = TO_DATE(#{DEUNGGI_DATE}, 'yyyy-mm-dd')
, dom_DEUNGGI_NO = #{DEUNGGI_NO}
, dom_DEUNGGISO = #{DEUNGGISO}
, dom_PNU = #{IN_PNU}
, dom_JIMOK_TEXT = #{JIMOK_TEXT}
, dom_CHUIDEUK_DATE = TO_DATE(#{CHUIDEUK_DATE}, 'yyyy-mm-dd')

WHERE dom_DOPCO_NO = #{DOPCO_NO}

mapper.delete:
DELETE FROM DOPCO_ATCFILE
WHERE da_DOPCO_NO = #{DOPCO_NO}

	AND da_FILE_SEQ = #{SEQ}

mapper.update:
UPDATE DOPCO_ATCFILE SET
DOPCO_NO = #{DOPCO_NO}
WHERE SEQ = #{FILESEQ}

UPDATE DOPCO_MASTER
SET
dom_CANCEL_YN = 'Y'
,dom_CANCEL_DATE = TO_DATE(#{CANCLEDATE},'yyyy-mm-dd')
,dom_CANCEL_NAME = #{USERNAME}
,dom_CANCEL_EMPCD = #{EMPCD}
WHERE dom_DOPCO_NO = #{DOPCO_NO}

mapper.select:
select TO_CHAR(CANCLE_DATE, 'yyyy-mm-dd') AS CANCLE_DATE, DOPCO_NO, RIGHT_ADDR, TO_CHAR(RIGHT_DATE, 'yyyy-mm-dd') AS RIGHT_DATE, RIGHT_MONEY, RIGHT_NAME, RIGHT_PHONE, RIGHT_UNAME
from DOPCO_RIGHT
where DOPCO_NO = #{DOPCO_NO}

SELECT *
FROM DOPCO_ATCFILE
WHERE DOPCO_NO = #{DOPCO_NO}

	AND SEQ = #{SEQ}


	AND FILE_SEQ = #{FILE_SEQ}

ORDER BY SEQ DESC
