mapper.select:
select * from gover_master where 1=1
and gm_gover_no=#{idx}
and gm_idx=cast(#{index} as integer)

select *,(row_number() over()) as no from gover_master gm left join gover_permit gp on gp.gp_gover_no=gm.gm_gover_no where 1=1
and gp.gp_gover_no=#{idx}
AND gm.gm_dockey IS NOT null AND gp.gp_dockey IS NOT null

select *,(row_number() over()) as no from gover_modify where 1=1
and gm_gover_no=#{idx}
order by gm_modify_date desc

SELECT *, (row_number() OVER()) AS no, r.pnu_count FROM ( SELECT gm.gm_idx AS index, gm.gm_gover_no AS idx, gm.gm_yongdo AS yongdo, 0 AS gidx, gm.gm_pipe_name AS pipename, gm.gm_sun_gubun AS sun_gubun, gm.gm_pipe_meter AS pipe_meter, gm.gm_cancel_yn AS cancel_yn, gm.gm_cancel_date AS cancel_date, gm.gm_cancel_name AS cancel_name, gm.gm_cancel_empcd AS cancel_empcd, gm.gm_use_purpos AS use_purpos, gm.gm_gover_st_date AS gover_st_date, gm.gm_gover_ed_date AS gover_ed_date, CONCAT(gm.gm_gover_st_date, ' ~ ', gm.gm_gover_ed_date) AS period, gm.gm_pmt_status AS pmt_status, gm.gm_dockey AS dockey, gm.gm_pmt_office AS pmt_office, gm.gm_adm_office AS adm_office, gm.gm_office_depart AS office_depart, gm.gm_office_charege AS office_charege, gm.gm_office_contact AS office_contact, gm.gm_office_mobile AS office_mobile, gm.gm_gover_period AS gover_period, gm.gm_pipe_meter2 AS pipe_meter2, gm.gm_save_status AS save_status, gm.gm_newregreason AS newregreason, gm.gm_occunonpayreason AS occunonpayreason, gm.gm_permpossyn AS permpossyn, gm.gm_occuprepayyn AS occuprepayyn, gm.gm_occuprepaydate AS occuprepaydate, gm.gm_udate AS udate, gm.gm_jisa AS jisa, gm.gm_gover_no AS gover_no, gm.gm_wdate AS wdate, gp.gp_sido_nm AS sido_nm, gp.gp_sgg_nm AS sgg_nm, gp.gp_emd_nm AS emd_nm, gp.gp_ri_nm AS ri_nm, gp.gp_jibun AS jibun, TRIM(CONCAT(gp.gp_sido_nm, ' ', gp.gp_sgg_nm, ' ', gp.gp_ri_nm, ' ', gp.gp_jibun)) AS address, gp.gp_gover_length AS gover_length, gp.gp_gover_area AS gover_area, gp.gp_jimok_text AS jimok_text, gpm.gp_pay_date AS pay_date, gpm.gp_pay_money AS pay_money, (SELECT COUNT(*) FROM gover_pnu WHERE gover_pnu.gp_gover_no = gm.gm_gover_no) AS pnu_count ,gpm.gp_pmt_no AS pmt_no ,gpm.gp_pay_vat as pay_vat ,gpm.gp_pmt_st_date as pmt_st_date ,gpm.gp_pmt_ed_date as pmt_ed_date ,gpm.gp_pay_way as pay_way
FROM gover_master gm LEFT JOIN GOVER_PNU gp ON gm.gm_gover_no = gp.gp_gover_no LEFT JOIN GOVER_PERMIT gpm ON gm.gm_gover_no = gpm.gp_gover_no WHERE 1 = 1
and gm.gm_jisa=#{jisa}
ORDER BY ${orderCol} ${desc}
) AS r where 1=1
and r.gover_no=#{gover_no}
and r.use_purpos=#{use_purpos}
and r.pmt_office=#{pmt_office}
and r.adm_office=#{adm_office}
and r.idx like '%${idx}%'
and r.address like '%${address}%'
and (SELECT COUNT(o.address) AS overlap FROM( ( SELECT trim(concat(gm.gm_sido_nm,' ',gm.gm_sgg_nm,' ',gm.gm_ri_nm,' ',gm.gm_jibun)) as address FROM gover_master gm LEFT JOIN GOVER_PNU gp ON gm.gm_gover_no = gp.gp_gover_no where 1=1 ) ) as o WHERE o.address = r.address GROUP BY o.address ) >1
limit #{length} offset #{start}

