     INTO GOVER_PNU (
           gp_SEQ
           , gp_GOVER_NO
           , gp_PNU
           , gp_ADDRCODE
           , gp_SIDO_NM
           , gp_SGG_NM
           , gp_EMD_NM
           , gp_RI_NM
           , gp_JIBUN
