           , gp_JIMOK_TEXT
           , gp_GOVER_LENGTH
           , gp_GOVER_AREA
           , gp_ADM_OFFICE
           , gp_USE_PURPOS
           , gp_REP_FLAG
           , gp_PIPE_OVERLAP_YN
        )
VALUES
	( nextval('gover_pnu_seq')
        , #{GOVER_NO}
        , #{IN_PNU}
        , #{ADDRCODE}
        , #{SIDO_NM}
        , #{SGG_NM}
        , #{EMD_NM}
		, #{RI_NM}
		, #{JIBUN}
		, #{JIBUN_FULL}
		, #{GOVEROWNYN}
		, nullif(#{JIJUK_AREA},'')::numeric
		, #{JIMOK_TEXT}
		, #{GOVER_LENGTH}::numeric
		, #{GOVER_AREA}::numeric
		, #{ADM_OFFICE}
		, #{USE_PURPOS}
		, #{REP_FLAG}
		, #{PIPEYN}
    )

