mapper.select:
		SELECT *
		FROM GOVER_ATCFILE
		WHERE ga_GOVER_NO = #{FILENO}


			AND ga_FILE_SEQ = #{FILE_SEQ}



			AND ga_PMT_NO = #{PMTNO}

		ORDER BY ga_SEQ DESC
                , ga_FILE_PATH DESC

