WHERE gp_GOVER_NO = #{GOVER_NO}

mapper.select:
SELECT TO_CHAR(GP.gp_PAY_DATE, 'YYYY') AS PAY_YR, GP.gp_seq AS SEQ, -- SEQ는 숫자라서 TO_CHAR 생략 가능 TO_CHAR(GP.gp_PAY_DATE, 'YYYY-MM-DD') AS PAY_DATE, GP.gp_pay_money as PAY_MONEY, TO_CHAR(GP.gp_PMT_ST_DATE, 'YYYY-MM-DD') AS PMT_ST_DATE, TO_CHAR(GP.gp_PMT_ED_DATE, 'YYYY-MM-DD') AS PMT_ED_DATE, GP.gp_pmt_no as PMT_NO, PA.pa_status as STATUS, PA.pa_out_flag as OUT_FLAG, PA.pa_deptcd as DEPTCD, PA.pa_updeptcd as UPDEPTCD, PA.pa_out_url as OUT_URL, GP.gp_dockey as DOCKEY, GP.gp_buseo as BUSEO, GP.gp_dosiplan as DOSIPLAN, GP.gp_echo_no as ECHO_NO, TO_CHAR(GP.gp_GOVER_AREA, 'FM999999999.00') AS GOVER_AREA, -- 숫자 포맷 처리 TO_CHAR(GP.gp_GOVER_LENGTH, 'FM999999999.00') AS GOVER_LENGTH, -- 숫자 포맷 처리 GP.gp_gover_no as GOVER_NO, TO_CHAR(GP.gp_NEXT_DATE, 'YYYY-MM-DD') AS NEXT_DATE, GP.gp_nm as NM, GP.gp_pay_way as PAY_WAY, GP.gp_phone_number as PHONE_NUMBER, TO_CHAR(GP.gp_PMT_FIRST_DATE, 'YYYY-MM-DD') AS PMT_FIRST_DATE, GP.gp_pmt_name as PMT_NAME, GP.gp_pmt_office as PMT_OFFICE, GP.gp_adm_office as ADM_OFFICE, GP.gp_siseolname as SISEOLNAME, TO_CHAR(GP.gp_UPDATE_DATE, 'YYYY-MM-DD') AS UPDATE_DATE FROM GOVER_PERMIT GP LEFT JOIN PLMS_APPROVAL PA ON GP.gp_DOCKEY = PA.pa_DOCKEY WHERE GP.gp_gover_no = #{GOVER_NO} AND GP.gp_PAY_DATE IS NOT NULL AND (PA.pa_STATUS IS NOT NULL AND PA.pa_STATUS NOT IN ('X'))
AND GP.gp_SEQ = #{SEQ}
AND GP.gp_SEQ = #{PMTNO}
ORDER BY GP.gp_GOVER_NO ASC , GP.gp_SEQ DESC

