mapper.select:
select * from notset_master nm LEFT JOIN notset_souja ns ON ns.ns_notset_no=nm.nm_notset_no where 1=1
and nm.nm_notset_no=#{idx}
and nm_idx=cast(${index} as varchar)

select * ,concat(nm_sido_nm,' ',nm_sgg_nm,' ',nm_ri_nm,' ',nm_jibun) as address from notset_master where 1=1
and nm_notset_no=#{idx}
and nm_idx=cast(#{index} as integer)
