INSERT INTO JISANG_STAT (
        SEQ,
        SAVE_YEAR,
        SAVE_QUARTER,
        SAVE_DATE,
        PROCESS_DATE,
        SAYUJI_N,
        SAYUJI_Y_Y,
        SAYUJI_Y_N,
        SAYUJI_TOTAL,
        GUKYUJI_Y,
        GUKYUJI_J,
        GUKYUJI_N,
        GUKYUJI_TOTAL,
        FILE_PATH,
        FILE_NAME
) VALUES (
        (SELECT NVL(MAX(SEQ), 0) + 1 FROM JISANG_STAT S),
        #{SAVE_YEAR},
        #{SAVE_QUARTER},
        Now(),
        #{PROCESS_DATE},
        #{SAYUJI_N},
        #{SAYUJI_Y_Y},
        #{SAYUJI_Y_N}
        #{SAYUJI_TOTAL}
        #{GUKYUJI_Y}
        #{GUKYUJI_J}
        #{GUKYUJI_N}
        #{GUKYUJI_TOTAL}
        #{FILE_PATH}
        #{FILE_NAME}
)

mapper.select:
SELECT TTT.TYPE, COALESCE(TTT.REGISTED_YN, 'NONE') AS REGISTED_YN, COALESCE(TTT.PERMITTED_YN, 'NONE') AS PERMITTED_YN, TTT.DEPTH1, TTT.DEPTH2, TTT.DEPTH3, TTT.DEPTH1_NAME, TTT.DEPTH2_NAME, TTT.DEPTH3_NAME, TTT.POTENTIAL_ISSUE_CNT, TTT.MINWON_CNT, ROUND(COALESCE(TTT.MINWON_CNT / NULLIF(TTT.MINWON_TOTAL_CNT, 0), 0) * 100, 2) AS MINWON_PERCENT, ROUND(COALESCE(TTT.POTENTIAL_ISSUE_CNT / NULLIF(TTT.PPI_TOTAL, 0), 0) * 100, 2) AS PPI_PER, TTT.PPI_TOTAL, TTT.MINWON_TOTAL_CNT FROM ( SELECT TT.TYPE, TT.REGISTED_YN, TT.PERMITTED_YN, TT.DEPTH1, TT.DEPTH2, TT.DEPTH3, TT.DEPTH1_NAME, TT.DEPTH2_NAME, TT.DEPTH3_NAME, TT.POTENTIAL_ISSUE_CNT, SUM(TT.POTENTIAL_ISSUE_CNT) OVER () AS PPI_TOTAL, TT.MINWON_CNT, SUM(TT.MINWON_CNT) OVER () AS MINWON_TOTAL_CNT FROM ( SELECT SUBSTRING(TN.DEPTH3, 1, 1) AS TYPE, TN.DEPTH1, TN.DEPTH2, TN.DEPTH3, COALESCE(PIC1.CODE_NAME, '이슈유형 미매칭') AS DEPTH1_NAME, COALESCE(PIC2.CODE_NAME, '이슈유형 미매칭') AS DEPTH2_NAME, COALESCE(TN.DEPTH3_NAME, '이슈유형 미매칭') AS DEPTH3_NAME, COUNT(T.MASTER_NO) AS POTENTIAL_ISSUE_CNT, ( SELECT COUNT(*) FROM MINWON_MASTER MM WHERE TN.DEPTH1 = COALESCE(MM.mm_MW_CODE1, 'NONE') AND TN.DEPTH2 = COALESCE(MM.mm_MW_CODE2, 'NONE') AND TN.DEPTH3 = COALESCE(MM.mm_MW_CODE3, 'NONE') AND MM.mm_DEL_FLAG != 'Y' AND MM.mm_STATUS IN ('2', '3', '4')
AND MM.mm_MW_OCCUR_DATE >= #{SEARCH_START_DAY}
AND MM.mm_MW_OCCUR_DATE <= to_date(#{SEARCH_END_DAY},'YYYYMMDD')
AND MM.mm_STATUS = #{SEARCH_STATUS}
AND MM.mm_JISA = #{SEARCH_JISA}
) AS MINWON_CNT, TN.REGISTED_YN, TN.PERMITTED_YN FROM ( SELECT JOIN_MST.MASTER_NO, JOIN_MST.SIDO_NM, JOIN_MST.SGG_NM, JOIN_MST.EMD_NM, JOIN_MST.RI_NM, JOIN_MST.JIBUN, JOIN_MST.PNU, JOIN_MST.ADDRCODE, JOIN_MST.JISANG_STATUS, CASE WHEN JOIN_MST.JISANG_STATUS != 'GOVER' THEN PPI.REGISTED_YN ELSE PPI.REGISTED_YN END AS REGISTED_YN, CASE WHEN JOIN_MST.JISANG_STATUS != 'GOVER' THEN PPI.PERMITTED_YN ELSE PPI.PERMITTED_YN END AS PERMITTED_YN, COALESCE(CASE WHEN JOIN_MST.JISANG_STATUS != 'GOVER' THEN PPI.CODE_DEPTH1 ELSE PPI.CODE_DEPTH1 END, 'NONE') AS CODE_DEPTH1, COALESCE(CASE WHEN JOIN_MST.JISANG_STATUS != 'GOVER' THEN PPI.CODE_DEPTH2 ELSE PPI.CODE_DEPTH2 END, 'NONE') AS CODE_DEPTH2, COALESCE(CASE WHEN JOIN_MST.JISANG_STATUS != 'GOVER' THEN PPI.CODE_DEPTH3 ELSE PPI.CODE_DEPTH3 END, 'NONE') AS CODE_DEPTH3 FROM JIJUK_2024 JIJUK_MST LEFT JOIN ( SELECT JM.jm_JISANG_NO AS MASTER_NO, 'JISANG' AS JISANG_STATUS, JM.jm_jisa as JISA, JM.jm_sido_nm as SIDO_NM, JM.jm_sgg_nm as SGG_NM, JM.jm_emd_nm as EMD_NM, JM.jm_ri_nm as RI_NM, JM.jm_jibun as JIBUN, JM.jm_pnu as PNU, RPAD(JM.jm_ADDRCODE, 10, '0') AS ADDRCODE, JM.jm_jasan_no as JASAN_NO, TO_CHAR(JM.jm_CHUIDEUK_DATE, 'YYYY-MM-DD') AS CHUIDEUK_DATE, JM.jm_chuideuk_money as CHUIDEUK_MONEY, JM.jm_jasan_money as JASAN_MONEY, NULL AS PMT_NO, NULL AS PAY_MONEY, NULL AS PAY_DATE, NULL AS PMT_ST_DATE, NULL AS PMT_ED_DATE, NULL AS GOVER_LENGTH, JM.jm_PYEONIB_AREA + COALESCE( (SELECT SUM(S.jm_PYEONIB_AREA) FROM JISANG_MERGE S WHERE S.jm_REP_JISANG_NO = JM.jm_JISANG_NO), 0) AS GOVER_AREA, NULL AS PIPE_METER, NULL AS PIPE_METER2, JS.SOUJA_NAME, ( SELECT COUNT(js_JISANG_NO) - 1 FROM JISANG_SOUJA WHERE js_JISANG_NO = JM.jm_JISANG_NO ) AS SOUJA_CNT, JM.jm_gover_own_yn as GOVER_OWN_YN, JM.jm_jijuk_area as JIJUK_AREA, JM.jm_comple_yn as COMPLE_YN, JM.jm_permitted_yn as PERMITTED_YN FROM JISANG_MASTER JM LEFT JOIN ( SELECT js_jisang_no as JISANG_NO, js_souja_name as SOUJA_NAME, ROW_NUMBER() OVER (PARTITION BY js_JISANG_NO ORDER BY js_JISANG_NO ASC) AS ROW_IDX FROM JISANG_SOUJA GROUP BY js_JISANG_NO, js_SOUJA_NAME ) JS ON JM.jm_JISANG_NO = JS.JISANG_NO WHERE (JM.jm_CANCLE_YN IS NULL OR JM.jm_CANCLE_YN != 'Y')
UNION ALL
SELECT GP.gp_GOVER_NO AS MASTER_NO, 'GOVER' AS JISANG_STATUS, ( SELECT S.gm_jisa as JISA FROM GOVER_MASTER S WHERE S.gm_GOVER_NO = GP.gp_GOVER_NO ) AS JISA, GP.gp_sido_nm as SIDO_NM, GP.gp_sgg_nm as SGG_NM, GP.gp_emd_nm as EMD_NM, GP.gp_ri_nm as RI_NM, GP.gp_jibun as JIBUN, GP.gp_pnu as PNU, GP.gp_addrcode as ADDRCODE, NULL AS JASAN_NO, NULL AS CHUIDEUK_DATE, NULL AS CHUIDEUK_MONEY, NULL AS JASAN_MONEY, GPE.PMT_NO, GPE.PAY_MONEY, TO_CHAR(GPE.PAY_DATE, 'YYYY-MM-DD') AS PAY_DATE, TO_CHAR(GM.gm_GOVER_ST_DATE, 'YYYY-MM-DD') AS PMT_ST_DATE, TO_CHAR(GM.gm_GOVER_ED_DATE, 'YYYY-MM-DD') AS PMT_ED_DATE, GP.gp_gover_length as GOVER_LENGTH, GP.gp_gover_area as GOVER_AREA, (SELECT S.gm_PIPE_METER FROM GOVER_MASTER S WHERE S.gm_GOVER_NO = GP.gp_GOVER_NO) AS PIPE_METER, (SELECT S.gm_PIPE_METER2 FROM GOVER_MASTER S WHERE S.gm_GOVER_NO = GP.gp_GOVER_NO) AS PIPE_METER2, NULL AS SOUJA_NAME, 0 AS SOUJA_CNT, GP.gp_gover_own_yn as GOVER_OWN_YN, GP.gp_jijuk_area as JIJUK_AREA, '' AS COMPLE_YN, '' AS PERMITTED_YN FROM GOVER_PNU GP LEFT JOIN ( SELECT S.gp_gover_no as GOVER_NO, S.gp_pay_money as PAY_MONEY, S.gp_pay_vat as PAY_VAT, S.gp_pay_date as PAY_DATE, S.gp_pay_way as PAY_WAY, S.gp_pmt_no as PMT_NO, S.gp_pmt_st_date as PMT_ST_DATE, S.gp_pmt_ed_date as PMT_ED_DATE FROM GOVER_PERMIT S INNER JOIN ( SELECT MAX(SS.gp_SEQ) AS SEQ, SS.gp_gover_no as GOVER_NO FROM GOVER_PERMIT SS WHERE SS.gp_PMT_NO IS NOT NULL AND SS.gp_DOCKEY IN ( SELECT SSS.pa_dockey as DOCKEY FROM PLMS_APPROVAL SSS WHERE SSS.pa_STATUS IS NOT NULL AND SSS.pa_STATUS != 'X' ) GROUP BY SS.gp_GOVER_NO ) SS ON S.gp_SEQ = SS.SEQ AND S.gp_GOVER_NO = SS.GOVER_NO ) GPE ON GP.gp_GOVER_NO = GPE.GOVER_NO LEFT JOIN GOVER_MASTER GM ON GP.gp_GOVER_NO = GM.gm_GOVER_NO WHERE GM.gm_SAVE_STATUS = 'A' AND GM.gm_CANCLE_YN IS NULL
UNION ALL
SELECT NM.nm_NOTSET_NO AS MASTER_NO, 'NOTSET' AS JISANG_STATUS, NM.nm_jisa as JISA, NM.nm_sido_nm as SIDO_NM, NM.nm_sgg_nm as SGG_NM, NM.nm_emd_nm as EMD_NM, NM.nm_ri_nm as RI_NM, NM.nm_jibun as JIBUN, NM.nm_pnu as PNU, NM.nm_addrcode as ADDRCODE, NULL AS JASAN_NO, NULL AS CHUIDEUK_DATE, NULL AS CHUIDEUK_MONEY, NULL AS JASAN_MONEY, NULL AS PMT_NO, NULL AS PAY_MONEY, NULL AS PAY_DATE, NULL AS PMT_ST_DATE, NULL AS PMT_ED_DATE, NULL AS GOVER_LENGTH, NULL AS GOVER_AREA, NULL AS PIPE_METER, NULL AS PIPE_METER2, NS.SOUJA_NAME, ( SELECT COUNT(ns_NOTSET_NO) - 1 FROM NOTSET_SOUJA WHERE ns_NOTSET_NO = NM.nm_NOTSET_NO ) AS SOUJA_CNT, NM.nm_gover_own_yn as GOVER_OWN_YN, NM.nm_jijuk_area as JIJUK_AREA, '' AS COMPLE_YN, '' AS PERMITTED_YN FROM NOTSET_MASTER NM LEFT JOIN ( SELECT ns_notset_no as NOTSET_NO, ns_souja_name as SOUJA_NAME, ROW_NUMBER() OVER (PARTITION BY ns_NOTSET_NO ORDER BY ns_NOTSET_NO ASC) AS ROW_IDX FROM NOTSET_SOUJA GROUP BY ns_NOTSET_NO, ns_SOUJA_NAME ) NS ON NM.nm_NOTSET_NO = NS.NOTSET_NO WHERE NM.nm_DEL_FLAG = 'N'
UNION ALL
SELECT DM.dom_DOPCO_NO AS MASTER_NO, 'DOPCO' AS JISANG_STATUS, DM.dom_jisa as JISA, DM.dom_sido_nm as SIDO_NM, DM.dom_sgg_nm as SGG_NM, DM.dom_emd_nm as EMD_NM, DM.dom_ri_nm as RI_NM, DM.dom_jibun as JIBUN, DM.dom_pnu as PNU, DM.dom_addrcode as ADDRCODE, DM.dom_jasan_no as JASAN_NO, TO_CHAR(DM.dom_DEUNGGI_DATE, 'YYYY-MM-DD') AS CHUIDEUK_DATE, NULL AS CHUIDEUK_MONEY, DM.dom_JASAN_MONEY::numeric AS JASAN_MONEY, NULL AS PMT_NO, NULL AS PAY_MONEY, NULL AS PAY_DATE, NULL AS PMT_ST_DATE, NULL AS PMT_ED_DATE, NULL AS GOVER_LENGTH, NULL AS GOVER_AREA, NULL AS PIPE_METER, NULL AS PIPE_METER2, DM.dom_souja_name as SOUJA_NAME, 0 AS SOUJA_CNT, DM.dom_gover_own_yn as GOVER_OWN_YN, DM.dom_jijuk_area as JIJUK_AREA, '' AS COMPLE_YN, '' AS PERMITTED_YN FROM DOPCO_MASTER DM WHERE DM.dom_CANCLE_YN IS NULL ) JOIN_MST ON JOIN_MST.PNU = JIJUK_MST.PNU
AND JOIN_MST.JISA = #{SEARCH_JISA}
LEFT JOIN ( SELECT S.* FROM ( SELECT RANK() OVER (PARTITION BY RPAD(PPI.addrcode, 10, '0'), PPI.JIBUN ORDER BY PPI.ISSUE_DATE DESC) AS RANK, RPAD(PPI.addrcode, 10, '0') AS ADDRCODE, PPI.jibun as JIBUN, PPI.code_depth1 as CODE_DEPTH1, PPI.code_depth2 as CODE_DEPTH2, PPI.code_depth3 as CODE_DEPTH3, PPI.registed_yn as REGISTED_YN, PPI.permitted_yn as PERMITTED_YN, PPI.pnu as PNU, PPI.del_flag as DEL_FLAG FROM plms_POTENTIAL_ISSUE PPI WHERE PPI.DEL_FLAG != 'Y' ) S WHERE S.RANK = 1 ) PPI ON RPAD(JOIN_MST.ADDRCODE, 10, '0') = PPI.ADDRCODE
AND JOIN_MST.JIBUN = PPI.JIBUN
LEFT JOIN GOVER_POTENTIAL_ISSUE GPI ON JOIN_MST.MASTER_NO = GPI.GOVER_NO AND GPI.DEL_FLAG != 'Y'
) T LEFT JOIN ( SELECT SUBSTRING(A.CODE, 1, 4) || '0000' AS DEPTH1, SUBSTRING(A.CODE, 1, 6) || '00' AS DEPTH2, A.CODE AS DEPTH3, A.CODE_NAME AS DEPTH3_NAME, A.REGISTED_YN, A.PERMITTED_YN FROM PLMS_ISSUE_CODE A WHERE A.CODE_DEPTH = 3
UNION ALL
SELECT 'NONE', 'NONE', 'NONE', NULL, NULL, NULL
) TN ON TN.DEPTH1 = T.CODE_DEPTH1 LEFT JOIN PLMS_ISSUE_CODE PIC1 ON TN.DEPTH1 = PIC1.CODE LEFT JOIN PLMS_ISSUE_CODE PIC2 ON TN.DEPTH2 = PIC2.CODE
AND TN.DEPTH1 = #{SEARCH_CODE_1}
AND TN.DEPTH2 = #{SEARCH_CODE_2}
AND TN.DEPTH3 = #{SEARCH_CODE_3}
AND SUBSTR(TN.DEPTH3, 1, 2) = #{SEARCH_REGISTED}
AND TN.PERMITTED_YN = #{SEARCH_PERMITTED}
GROUP BY TN.DEPTH1, TN.DEPTH2, TN.DEPTH3, TN.REGISTED_YN, TN.PERMITTED_YN, PIC1.CODE_NAME, PIC2.CODE_NAME, TN.DEPTH3_NAME ORDER BY SUBSTRING(TN.DEPTH3, 1, 1) ASC, TN.REGISTED_YN DESC, TN.DEPTH3 ASC ) TT ) TTT

select month from static_deadline where desc1 is not null
and year = #{SELECT_YYYY}
group by month order by month

with all_tb as ( select * from static_deadline_info where 1=1
and desc1 = #{JISA}
and jisang_status = #{STATUS}
), ref_tb as (select * from static_deadline where YEAR = {YYYY_REF} and month = {MM_REF} and desc1 is not null), tg_tb as (select * from static_deadline where year = {YYYY_TG} and month = {MM_TG} and desc1 is not null), add_cnt as ( select a.desc1,a.jisang_status, 'NEW' as gubun, count(*) as cnt from ref_tb a left join tg_tb b on a.sido_nm = b.sido_nm and a.sgg_nm= b.sgg_nm and a.emd_nm = b.emd_nm and a.ri_nm = b.ri_nm and a.jibun = b.jibun where b.juso is null group by a.desc1,a.jisang_status union all select a.desc1,'ALL', 'NEW' as gubun, count(*) as cnt from ref_tb a left join tg_tb b on a.sido_nm = b.sido_nm and a.sgg_nm= b.sgg_nm and a.emd_nm = b.emd_nm and a.ri_nm = b.ri_nm and a.jibun = b.jibun where b.juso is null group by a.desc1 ), rm_cnt as ( select a.desc1,a.jisang_status, 'DEL' as gubun, count(*) as cnt from tg_tb a left join ref_tb b on a.sido_nm = b.sido_nm and a.sgg_nm= b.sgg_nm and a.emd_nm = b.emd_nm and a.ri_nm = b.ri_nm and a.jibun = b.jibun where b.juso is null group by a.desc1,a.jisang_status union all select a.desc1,'ALL', 'NEW' as gubun, count(*) as cnt from tg_tb a left join ref_tb b on a.sido_nm = b.sido_nm and a.sgg_nm= b.sgg_nm and a.emd_nm = b.emd_nm and a.ri_nm = b.ri_nm and a.jibun = b.jibun where b.juso is null group by a.desc1 ) , ref_stat as ( select desc1,jisang_status,count(*) as cnt from ref_tb group by desc1,jisang_status union all select desc1,'ALL' as jisang_status,count(*) from ref_tb group by desc1 ), tg_stat as ( select desc1,jisang_status,count(*) as cnt from tg_tb group by desc1,jisang_status union all select desc1,'ALL' as jisang_status,count(*) from tg_tb group by desc1 ) select a.desc1,a.jisang_status, coalesce(b.cnt,0) as ref_cnt , coalesce(c.cnt,0) as tg_cnt,coalesce(d.cnt,0) add_cnt,coalesce(e.cnt,0) as del_cnt from all_tb a left join ref_stat b on a.desc1= b.desc1 and a.jisang_status = b.jisang_status left join tg_stat c on a.desc1= c.desc1 and a.jisang_status = c.jisang_status left join add_cnt d on a.desc1= d.desc1 and a.jisang_status = d.jisang_status left join rm_cnt e on a.desc1= e.desc1 and a.jisang_status = e.jisang_status order by a.desc1_num, a.status_num

select
	desc1,
	juso,
	jisang_status
from 
	static_deadline 
where 
	1=1
	and year= {YYYY}
	and month = {MM}
	and desc1 = {JISA}
	and jisang_status = {STATUS}

SELECT mm.mm_jisa, COUNT(*) AS total_count, COUNT(*) FILTER (WHERE mm.mm_status = '1') AS status_1_count, '임시저장' AS status_1_title, COUNT(*) FILTER (WHERE mm.mm_status = '2') AS status_2_count, '민원발생' AS status_2_title, COUNT(*) FILTER (WHERE mm.mm_status = '3') AS status_3_count, '대응방안수립' AS status_3_title, COUNT(*) FILTER (WHERE mm.mm_status = '4') AS status_4_count, '협의중' AS status_4_title, COUNT(*) FILTER (WHERE mm.mm_status = '5') AS status_5_count, '완료' AS status_5_title FROM minwon_master mm where 1=1
and mm.mm_jisa in
#{jisa}
and mm.mm_status=#{status}
AND TO_CHAR(mm.mm_occur_date, 'YYYY-MM') = #{occurDate}
GROUP BY mm.mm_jisa ORDER BY mm.mm_jisa

SELECT T.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY SEQ DESC) AS RN, SEQ, SAVE_YEAR, SAVE_QUARTER, SAVE_DATE::date as SAVE_DATE, SAYUJI_N, SAYUJI_Y_Y, SAYUJI_Y_N, SAYUJI_TOTAL, GUKYUJI_J, GUKYUJI_Y, GUKYUJI_N, GUKYUJI_TOTAL, PROCESS_DATE, FILE_PATH, FILE_NAME FROM JISANG_STAT WHERE 1 = 1
AND SAVE_YEAR = #{SAVE_YEAR}
AND SAVE_QUARTER = #{SAVE_QUARTER}
ORDER BY SEQ DESC ) T WHERE 1 = 1
AND T.RN BETWEEN ((#{PAGE_CNT} * (#{PAGE_NUM} - 1)) + 1) AND (#{PAGE_CNT} * #{PAGE_NUM})


SELECT COUNT(*) 
  FROM JISANG_STAT 
 WHERE 1 = 1 


    AND SAVE_YEAR = #{SAVE_YEAR}


    AND SAVE_QUARTER = #{SAVE_QUARTER}
