mapper.select:
SELECT *, (row_number() OVER()) AS no FROM ( SELECT dm.dm_idx AS index, dm.dm_dosi_no AS idx, dm.dm_dosi_no AS dosi_no, dm.dm_sido_nm as sido_nm, dm.dm_sgg_nm as sgg_nm, dm.dm_emd_nm as emd_nm, dm.dm_ri_nm as ri_nm, dm.dm_jibun as jibun, dm.dm_jisa AS jisa, COALESCE(dm.dm_jijuk_area, NULL) AS jijuk_area, dm.dm_dosiplan AS dosiplan, dm.dm_addrcode as addrcode, dm.dm_pnu as pnu, dm.dm_jimok_text as jimok_text, dm.dm_toji_type AS toji_type, COALESCE(dm.dm_pipe_yn, 'N') AS pipe_yn, dm.dm_length as length, dm.dm_soyouja as souja, dm.dm_master_yn as master_yn, dm_dosi_seq as dosi_seq, di.di_business_nm AS business_nm, di.di_strdate AS strdate, di.di_enddate AS enddate, di.di_adm_office AS adm_office, di.di_business_oper AS business_oper, di.di_complete_date AS complete_date, di.di_complete_yn AS complete_yn, j.dd_dept_nm AS dept_nm, j.dd_manager AS manager, j.dd_contact_num AS contact_num, TRIM(CONCAT(dm.dm_sido_nm, ' ', dm.dm_sgg_nm, ' ', dm.dm_ri_nm, ' ', dm.dm_jibun)) AS address
FROM dosi_master dm LEFT JOIN dosi_info di ON di.di_dosi_no=dm.dm_dosi_no left JOIN (
SELECT dd.dd_dosi_no,dd.dd_dept_nm,dd.dd_manager,dd.dd_contact_num,dd.dd_dept_seq,(row_number() over()) as NO FROM dosi_dept dd
) j ON dm.dm_dosi_no=j.dd_dosi_no AND j.no=1
WHERE 1 = 1 ) AS r where 1=1
and r.jisa=#{jisa}
and r.complete_yn=#{complete_yn}
and r.strdate::date = #{start_date}::date
and r.enddate::date=#{end_date}::date
and r.address like '%${address}%'
ORDER BY ${orderCol} ${desc}
limit #{length} offset #{start}

SELECT count(*)
FROM ( SELECT dm.dm_idx AS index, dm.dm_dosi_no AS idx, dm.dm_dosi_no AS dosi_no, dm.dm_sido_nm as sido_nm, dm.dm_sgg_nm as sgg_nm, dm.dm_emd_nm as emd_nm, dm.dm_ri_nm as ri_nm, dm.dm_jibun as jibun, dm.dm_jisa AS jisa, COALESCE(dm.dm_jijuk_area, NULL) AS jijuk_area, dm.dm_dosiplan AS dosiplan, dm.dm_addrcode as addrcode, dm.dm_pnu as pnu, dm.dm_jimok_text as jimok_text, dm.dm_toji_type AS toji_type, COALESCE(dm.dm_pipe_yn, 'N') AS pipe_yn, dm.dm_length as length, dm.dm_soyouja as souja, dm.dm_master_yn as master_yn, dm_dosi_seq as dosi_seq, di.di_business_nm AS business_nm, di.di_strdate AS strdate, di.di_enddate AS enddate, di.di_adm_office AS adm_office, di.di_business_oper AS business_oper, di.di_complete_date AS complete_date, di.di_complete_yn AS complete_yn, j.dd_dept_nm AS dept_nm, j.dd_manager AS manager, j.dd_contact_num AS contact_num, TRIM(CONCAT(dm.dm_sido_nm, ' ', dm.dm_sgg_nm, ' ', dm.dm_ri_nm, ' ', dm.dm_jibun)) AS address
FROM dosi_master dm LEFT JOIN dosi_info di ON di.di_dosi_no=dm.dm_dosi_no left JOIN (
SELECT dd.dd_dosi_no,dd.dd_dept_nm,dd.dd_manager,dd.dd_contact_num,dd.dd_dept_seq,(row_number() over()) as NO FROM dosi_dept dd
) j ON dm.dm_dosi_no=j.dd_dosi_no AND j.no=1
WHERE 1 = 1
) AS r where 1=1
and r.jisa=#{jisa}
and r.complete_yn=#{complete_yn}
and r.strdate::date = #{start_date}::date
and r.enddate::date=#{end_date}::date
and r.address like '%${address}%'
ORDER BY ${orderCol} ${desc}

mapper.insert:
INSERT INTO DOSI_MASTER
(
dm_DOSI_SEQ
,dm_DOSI_NO
, dm_SIDO_NM
, dm_SGG_NM
, dm_EMD_NM
, dm_RI_NM
, dm_JIBUN
, dm_JISA
, dm_JIJUK_AREA
, dm_DOSIPLAN
, dm_USE_STATE
, dm_ADDRCODE
, dm_PNU
, dm_JIMOK_TEXT
, dm_TOJI_TYPE
, dm_PIPE_YN
, dm_LENGTH
, dm_SOYOUJA
, dm_MASTER_YN)
VALUES
(
nextval('DOSI_SEQ')
,#{DOSI_NO}
, #{SIDO_NM}
, #{SGG_NM}
, #{EMD_NM}
, #{RI_NM}
, #{JIBUN}
, #{JISA}
, nullif(#{JIJUK_AREA},'')::numeric
, #{DOSIPLAN}
,#{USE_STATE}
, #{ADDRCODE}
, #{PNU}
, #{JIMOK_TEXT}
, #{TOJI_TYPE}
, #{PIPE_YN}
, #{LENGTH}
, #{SOYOUJA}
, #{MASTER_YN}
)

INSERT INTO DOSI_INFO
(
di_DOSI_NO
, di_BUSINESS_NM,

	di_STRDATE,


	di_ENDDATE,

di_ADM_OFFICE
, di_BUSINESS_OPER
, di_USER_ID
)
VALUES
(
#{DOSI_NO}
, #{BUSINESS_NM},

	TO_DATE(#{STRDATE} || '00:00:00', 'YYYYMMDDHH24:MI:SS') ,


	TO_DATE(#{ENDDATE} || '00:00:00', 'YYYYMMDDHH24:MI:SS') ,

#{ADM_OFFICE}
,#{BUSINESS_OPER}
, #{USER_ID}
)
